targets.expr:
(
  label_replace(kube_node_role{role="master"}, "role", "master", "role", "master") *
  on(node) group_left() (
    (
      node_memory_MemTotal_bytes - node_memory_MemFree_bytes - node_memory_Buffers_bytes - node_memory_Cached_bytes - node_memory_SReclaimable_bytes
    ) 
  )
)
or
(
  label_replace(kube_node_role{role="node"}, "role", "worker", "role", "node") *
  on(node) group_left() (
    (
      node_memory_MemTotal_bytes - node_memory_MemFree_bytes - node_memory_Buffers_bytes - node_memory_Cached_bytes - node_memory_SReclaimable_bytes
    ) 
  )
)
